<?phpclass Application_Model_DbTable_Estudiante extends Zend_Db_Table_Abstract{    protected $_name = 'estudiante';	public function add($institucion, $ci, $apellidos, $nombres, $nivel, $codigo)	{		$data = array('institucion' => $institucion, 					  'codigo' => $codigo, 					  'ci' => $ci, 					  'apellidos' => $apellidos, 					  'nombres' => $nombres,					  'nivel' => $nivel);		try {			$this->insert($data);			return array('estado' => '1');		} catch (Exception $ex) {			return array('estado' => '0', 'msg' => $ex->getMessage());		}	}	public function buscar($idInstitucion)	{		$select = $this->select()->where('institucion = ?', $idInstitucion);		$rows = $this->fetchAll($select);		return $rows->toArray();	}	public function buscar2($codigo, $ci)	{		$select = $this->select()->where('codigo = ?', $codigo)								 ->orWhere('ci = ?', $ci);		$rows = $this->fetchAll($select);		return $rows->toArray();	}		public function reporte($nivel)	{		$select = $this->select()					   ->from($this, array('codigo', 'ci', 'apellidos', 'nombres', 'nivel'))				->where('nivel = ?', $nivel);		$rows = $this->fetchAll($select);		return $rows->toArray();	}		public function reporteSG()	{		$select = $this->select()					   ->from($this, array('codigo', 'ci', 'apellidos', 'nombres', 'nivel'));		$rows = $this->fetchAll($select);		return $rows->toArray();	}}