<?phpclass Application_Model_DbTable_Institucion extends Zend_Db_Table_Abstract{    protected $_name = 'institucion';	public function add($codigo, $nombre, $pais, $provincia, $canton, $ciudad, $direccion, $telefono, $email, $referencia, $banco)	{		$data = array('codigo' => $codigo, 					  'nombre' => $nombre, 					  'pais' => $pais, 					  'provincia' => $provincia,					  'canton' => $canton,					  'ciudad' => $ciudad,					  'direccion' => $direccion,					  'telefono' => $telefono,					  'email' => $email,					  'referencia_deposito' => $referencia,					  'banco' => $banco);		try {			$id = $this->insert($data);			return array('estado' => '1', 'id' => $id);		} catch (Exception $ex) {			return $ex->getMessage();		}	}	public function buscar($codigo)	{		$select = $this->select()->where('codigo = ?', $codigo);		$rows = $this->fetchAll($select);		return $rows->toArray();	}	public function reporte()	{		$select = $this->select()					   ->from($this, array('codigo', 'nombre', 'canton', 'direccion'))					   ->where('nombre NOT LIKE ?', '%sg%');		$rows = $this->fetchAll($select);		return $rows->toArray();	}		public function reporteSG()	{		$select = $this->select()					   ->from($this, array('codigo', 'nombre', 'canton', 'direccion'))				->where('nombre LIKE ?', '%sg%');		$rows = $this->fetchAll($select);		return $rows->toArray();	}}